import React, {useState} from "react";
import { Row, Col, Button, CardImg, Modal } from "react-bootstrap";

function Project(prop) {
  const [show, setShow] = useState(false);

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  return (
    <React.Fragment>
      <Col md={4} sm={12} className="work-items">
        <CardImg
          className="work-imagebox"
          src={prop.image}
        />

      <p className="work-title" onClick={handleShow}>{prop.name}
      </p>

      <Modal show={show} onHide={handleClose} aria-labelledby="contained-modal-title-vcenter" centered>
        <Modal.Header closeButton>
          <Modal.Title>{prop.name}</Modal.Title>
        </Modal.Header>
        <Modal.Body>
        <CardImg src={prop.image}/>
        {prop.decs}</Modal.Body>
        <Modal.Footer>
          <Button href={prop.deployed} className="" target="_blank" >
              Site Link
          </Button>
          <Button href={prop.repo} className=" " target="_blank"> 
              Github Repository 
          </Button>
          <Button variant="secondary" onClick={handleClose}>
              Close
          </Button>
        </Modal.Footer>
      </Modal>




      </Col>
    </React.Fragment>
  );
}
export default Project;
